INSERT INTO "equipment" VALUES(41,'Shield, light','Weapons','Martial Weapons','Light Melee Weapons','special','1d2',NULL,NULL,'1d3','special','x2',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(42,'Spiked armor','Weapons','Martial Weapons','Light Melee Weapons','special','1d4',NULL,NULL,'1d6','special','x2',NULL,NULL,'-',NULL,'Piercing',NULL,'You can outfit your armor with spikes, which can deal damage in a grapple or as a separate attack. See Armor for details.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(43,'Spiked shield, light','Weapons','Martial Weapons','Light Melee Weapons','special','1d3',NULL,NULL,'1d4','special','x2',NULL,NULL,'-',NULL,'Piercing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(44,'Sword, short','Weapons','Martial Weapons','Light Melee Weapons','10 gp','1d4',NULL,NULL,'1d6','2 lb.','19-20/x2',NULL,NULL,'-',NULL,'Piercing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(45,'Battleaxe','Weapons','Martial Weapons','One-Handed Melee Weapons','10 gp','1d6',NULL,NULL,'1d8','6 lb.','x3',NULL,NULL,'-',NULL,'Slashing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(46,'Flail','Weapons','Martial Weapons','One-Handed Melee Weapons','8 gp','1d6',NULL,NULL,'1d8','5 lb.','x2',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'With a flail, you get a +2 bonus on opposed attack rolls made to disarm an enemy (including the roll to avoid being disarmed if such an attempt fails). You can also use this weapon to make trip attacks. If you are tripped during your own trip attempt, you can drop the flail to avoid being tripped.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(47,'Longsword','Weapons','Martial Weapons','One-Handed Melee Weapons','15 gp','1d6',NULL,NULL,'1d8','4 lb.','19-20/x2',NULL,NULL,'-',NULL,'Slashing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(48,'Pick, heavy','Weapons','Martial Weapons','One-Handed Melee Weapons','8 gp','1d4',NULL,NULL,'1d6','6 lb.','x4',NULL,NULL,'-',NULL,'Piercing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(49,'Rapier','Weapons','Martial Weapons','One-Handed Melee Weapons','20 gp','1d4',NULL,NULL,'1d6','2 lb.','18-20/x2',NULL,NULL,'-',NULL,'Piercing',NULL,'You can use the Weapon Finesse feat to apply your Dexterity modifier instead of your Strength modifier to attack rolls with a rapier sized for you, even though it isn''t a light weapon for you. You can''t wield a rapier in two hands in order to apply 1-1/2 times your Strength bonus to damage.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(50,'Scimitar','Weapons','Martial Weapons','One-Handed Melee Weapons','15 gp','1d4',NULL,NULL,'1d6','4 lb.','18-20/x2',NULL,NULL,'-',NULL,'Slashing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(51,'Shield, heavy','Weapons','Martial Weapons','One-Handed Melee Weapons','special','1d3',NULL,NULL,'1d4','special','x2',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'You can bash with a shield instead of using it for defense. See Armor for details.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(52,'Spiked shield, heavy','Weapons','Martial Weapons','One-Handed Melee Weapons','special','1d4',NULL,NULL,'1d6','special','x2',NULL,NULL,'-',NULL,'Piercing',NULL,'You can bash with a spiked shield instead of using it for defense. See Armor for details.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(53,'Trident','Weapons','Martial Weapons','One-Handed Melee Weapons','15 gp','1d6',NULL,NULL,'1d8','4 lb.','x2',NULL,NULL,'10 ft.',NULL,'Piercing',NULL,'This weapon can be thrown. If you use a ready action to set a trident against a charge, you deal double damage on a successful hit against a charging character.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(54,'Warhammer','Weapons','Martial Weapons','One-Handed Melee Weapons','12 gp','1d6',NULL,NULL,'1d8','5 lb.','x3',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(55,'Falchion','Weapons','Martial Weapons','Two-Handed Melee Weapons','75 gp','1d6',NULL,NULL,'2d4','8 lb.','18-20/x2',NULL,NULL,'-',NULL,'Slashing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(56,'Glaive','Weapons','Martial Weapons','Two-Handed Melee Weapons','8 gp','1d8',NULL,NULL,'1d10','10 lb.','x3',NULL,NULL,'-',NULL,'Slashing',NULL,'A glaive has reach. You can strike opponents 10 feet away with it, but you can''t use it against an adjacent foe.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(57,'Greataxe','Weapons','Martial Weapons','Two-Handed Melee Weapons','20 gp','1d10',NULL,NULL,'1d12','12 lb.','x3',NULL,NULL,'-',NULL,'Slashing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(58,'Greatclub','Weapons','Martial Weapons','Two-Handed Melee Weapons','5 gp','1d8',NULL,NULL,'1d10','8 lb.','x2',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(59,'Flail, heavy','Weapons','Martial Weapons','Two-Handed Melee Weapons','15 gp','1d8',NULL,NULL,'1d10','10 lb.','19-20/x2',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'With a flail, you get a +2 bonus on opposed attack rolls made to disarm an enemy (including the roll to avoid being disarmed if such an attempt fails). You can also use this weapon to make trip attacks. If you are tripped during your own trip attempt, you can drop the flail to avoid being tripped.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(60,'Greatsword','Weapons','Martial Weapons','Two-Handed Melee Weapons','50 gp','1d10',NULL,NULL,'2d6','8 lb.','19-20/x2',NULL,NULL,'-',NULL,'Slashing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(61,'Guisarme','Weapons','Martial Weapons','Two-Handed Melee Weapons','9 gp','1d6',NULL,NULL,'2d4','12 lb.','x3',NULL,NULL,'-',NULL,'Slashing',NULL,'A guisarme has reach. You can strike opponents 10 feet away with it, but you can''t use it against an adjacent foe. You can also use it to make trip attacks. If you are tripped during your own trip attempt, you can drop the guisarme to avoid being tripped.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(62,'Halberd','Weapons','Martial Weapons','Two-Handed Melee Weapons','10 gp','1d8',NULL,NULL,'1d10','12 lb.','x3',NULL,NULL,'-',NULL,'Piercing or slashing',NULL,'If you use a ready action to set a halberd against a charge, you deal double damage on a successful hit against a charging character. You can use a halberd to make trip attacks. If you are tripped during your own trip attempt, you can drop the halberd to avoid being tripped.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(63,'Lance','Weapons','Martial Weapons','Two-Handed Melee Weapons','10 gp','1d6',NULL,NULL,'1d8','10 lb.','x3',NULL,NULL,'-',NULL,'Piercing',NULL,'A lance deals double damage when used from the back of a charging mount. It has reach, so you can strike opponents 10 feet away with it, but you can''t use it against an adjacent foe. While mounted, you can wield a lance with one hand.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(64,'Ranseur','Weapons','Martial Weapons','Two-Handed Melee Weapons','10 gp','1d6',NULL,NULL,'2d4','12 lb.','x3',NULL,NULL,'-',NULL,'Piercing',NULL,'A ranseur has reach. You can strike opponents 10 feet away with it, but you can''t use it against an adjacent foe. With a ranseur, you get a +2 bonus on opposed attack rolls made to disarm an opponent (including the roll to avoid being disarmed if such an attempt fails).','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(65,'Scythe','Weapons','Martial Weapons','Two-Handed Melee Weapons','18 gp','1d6',NULL,NULL,'2d4','10 lb.','x4',NULL,NULL,'-',NULL,'Piercing or slashing',NULL,'A scythe can be used to make trip attacks. If you are tripped during your own trip attempt, you can drop the scythe to avoid being tripped.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(66,'Longbow','Weapons','Martial Weapons','Ranged Weapons','75 gp','1d6',NULL,NULL,'1d8','3 lb.','x3',NULL,NULL,'100 ft.',NULL,'Piercing',NULL,'You need at least two hands to use a bow, regardless of its size. A longbow is too unwieldy to use while you are mounted. If you have a penalty for low Strength, apply it to damage rolls when you use a longbow. If you have a bonus for high Strength, you can apply it to damage rolls when you use a composite longbow (see below) but not a regular longbow.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(67,'Arrows (20)','Weapons','Martial Weapons','Ammunition','1 gp','-',NULL,NULL,'-','3 lb.','-',NULL,NULL,'-',NULL,'-',NULL,'An arrow used as a melee weapon is treated as a light improvised weapon (-4 penalty on attack rolls) and deals damage as a dagger of its size (critical multiplier x2). Arrows come in a leather quiver that holds 20 arrows. An arrow that hits its target is destroyed; one that misses has a 50% chance of being destroyed or lost.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(68,'Longbow, composite','Weapons','Martial Weapons','Ranged Weapons','100 gp','1d6',NULL,NULL,'1d8','3 lb.','x3',NULL,NULL,'110 ft.',NULL,'Piercing',NULL,'You need at least two hands to use a bow, regardless of its size. You can use a composite longbow while mounted. All composite bows are made with a particular strength rating (that is, each requires a minimum Strength modifier to use with proficiency). If your Strength bonus is less than the strength rating of the composite bow, you can''t effectively use it, so you take a -2 penalty on attacks with it. The default composite longbow requires a Strength modifier of +0 or higher to use with proficiency. A composite longbow can be made with a high strength rating to take advantage of an above-average Strength score; this feature allows you to add your Strength bonus to damage, up to the maximum bonus indicated for the bow. Each point of Strength bonus granted by the bow adds 100 gp to its cost. For purposes of weapon proficiency and similar feats, a composite longbow is treated as if it were a longbow.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(69,'Shortbow','Weapons','Martial Weapons','Ranged Weapons','30 gp','1d4',NULL,NULL,'1d6','2 lb.','x3',NULL,NULL,'60 ft.',NULL,'Piercing',NULL,'You need at least two hands to use a bow, regardless of its size. You can use a shortbow while mounted. If you have a penalty for low Strength, apply it to damage rolls when you use a shortbow. If you have a bonus for high Strength, you can apply it to damage rolls when you use a composite shortbow (see below) but not a regular shortbow.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(70,'Shortbow, composite','Weapons','Martial Weapons','Ranged Weapons','75 gp','1d4',NULL,NULL,'1d6','2 lb.','x3',NULL,NULL,'70 ft.',NULL,'Piercing',NULL,'You need at least two hands to use a bow, regardless of its size. You can use a composite shortbow while mounted. All composite bows are made with a particular strength rating (that is, each requires a minimum Strength modifier to use with proficiency). If your Strength bonus is lower than the strength rating of the composite bow, you can''t effectively use it, so you take a -2 penalty on attacks with it. The default composite shortbow requires a Strength modifier of +0 or higher to use with proficiency. A composite shortbow can be made with a high strength rating to take advantage of an above-average Strength score; this feature allows you to add your Strength bonus to damage, up to the maximum bonus indicated for the bow. Each point of Strength bonus granted by the bow adds 75 gp to its cost. For purposes of weapon proficiency and similar feats, a composite shortbow is treated as if it were a shortbow.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(71,'Kama','Weapons','Exotic Weapons','Light Melee Weapons','2 gp','1d4',NULL,NULL,'1d6','2 lb.','x2',NULL,NULL,'-',NULL,'Slashing',NULL,'The kama is a special monk weapon. This designation gives a monk wielding a kama special options. You can use a kama to make trip attacks. If you are tripped during your own trip attempt, you can drop the kama to avoid being tripped.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(72,'Nunchaku','Weapons','Exotic Weapons','Light Melee Weapons','2 gp','1d4',NULL,NULL,'1d6','2 lb.','x2',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'The nunchaku is a special monk weapon. This designation gives a monk wielding a nunchaku special options. With a nunchaku, you get a +2 bonus on opposed attack rolls made to disarm an enemy (including the roll to avoid being disarmed if such an attempt fails).','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(73,'Sai','Weapons','Exotic Weapons','Light Melee Weapons','1 gp','1d3',NULL,NULL,'1d4','1 lb.','x2',NULL,NULL,'10 ft.',NULL,'Bludgeoning',NULL,'With a sai, you get a +4 bonus on opposed attack rolls made to disarm an enemy (including the roll to avoid being disarmed if such an attempt fails). The sai is a special monk weapon. This designation gives a monk wielding a sai special options.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(74,'Siangham','Weapons','Exotic Weapons','Light Melee Weapons','3 gp','1d4',NULL,NULL,'1d6','1 lb.','x2',NULL,NULL,'-',NULL,'Piercing',NULL,'The siangham is a special monk weapon. This designation gives a monk wielding a siangham special options.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(75,'Sword, bastard','Weapons','Exotic Weapons','One-Handed Melee Weapons','35 gp','1d8',NULL,NULL,'1d10','6 lb.','19-20/x2',NULL,NULL,'-',NULL,'Slashing',NULL,'A bastard sword is too large to use in one hand without special training; thus, it is an exotic weapon. A character can use a bastard sword two-handed as a martial weapon.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(76,'Waraxe, dwarven','Weapons','Exotic Weapons','One-Handed Melee Weapons','30 gp','1d8',NULL,NULL,'1d10','8 lb.','x3',NULL,NULL,'-',NULL,'Slashing',NULL,'A dwarven waraxe is too large to use in one hand without special training; thus, it is an exotic weapon. A Medium character can use a dwarven waraxe two-handed as a martial weapon, or a Large creature can use it one-handed in the same way. A dwarf treats a dwarven waraxe as a martial weapon even when using it in one hand.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(77,'Whip','Weapons','Exotic Weapons','One-Handed Melee Weapons','1 gp','1d2',NULL,NULL,'1d3','Slashing','x2',NULL,NULL,'2 lb.',NULL,NULL,NULL,'A whip deals nonlethal damage. It deals no damage to any creature with an armor bonus of +1 or higher or a natural armor bonus of +3 or higher. The whip is treated as a melee weapon with 15-foot reach, though you don''t threaten the area into which you can make an attack. In addition, unlike most other weapons with reach, you can use it against foes anywhere within your reach (including adjacent foes). Using a whip provokes an attack of opportunity, just as if you had used a ranged weapon. You can make trip attacks with a whip. If you are tripped during your own trip attempt, you can drop the whip to avoid being tripped. When using a whip, you get a +2 bonus on opposed attack rolls made to disarm an opponent (including the roll to keep from being disarmed if the attack fails). You can use the Weapon Finesse feat to apply your Dexterity modifier instead of your Strength modifier to attack rolls with a whip sized for you, even though it isn''t a light weapon for you.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(78,'Axe, orc double','Weapons','Exotic Weapons','Two-Handed Melee Weapons','60 gp','1d6/1d6',NULL,NULL,'1d8/1d8','15 lb.','x3',NULL,NULL,'-',NULL,'Slashing',NULL,'An orc double axe is a double weapon. You can fight with it as if fighting with two weapons, but if you do, you incur all the normal attack penalties associated with fighting with two weapons, just as if you were using a one-handed weapon and a light weapon. A creature wielding an orc double axe in one hand can''t use it as a double weapon-only one end of the weapon can be used in any given round.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(79,'Chain, spiked','Weapons','Exotic Weapons','Two-Handed Melee Weapons','25 gp','1d6',NULL,NULL,'2d4','10 lb.','x2',NULL,NULL,'-',NULL,'Piercing',NULL,'A spiked chain has reach, so you can strike opponents 10 feet away with it. In addition, unlike most other weapons with reach, it can be used against an adjacent foe. You can make trip attacks with the chain. If you are tripped during your own trip attempt, you can drop the chain to avoid being tripped. When using a spiked chain, you get a +2 bonus on opposed attack rolls made to disarm an opponent (including the roll to avoid being disarmed if such an attempt fails). You can use the Weapon Finesse feat to apply your Dexterity modifier instead of your Strength modifier to attack rolls with a spiked chain sized for you, even though it isn''t a light weapon for you.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(80,'Flail, dire','Weapons','Exotic Weapons','Two-Handed Melee Weapons','90 gp','1d6/1d6',NULL,NULL,'1d8/1d8','10 lb.','x2',NULL,NULL,'-',NULL,'Bludgeoning',NULL,'A dire flail is a double weapon. You can fight with it as if fighting with two weapons, but if you do, you incur all the normal attack penalties associated with fighting with two weapons, just as if you were using a one-handed weapon and a light weapon. A creature wielding a dire flail in one hand can''t use it as a double weapon- only one end of the weapon can be used in any given round. When using a dire flail, you get a +2 bonus on opposed attack rolls made to disarm an enemy (including the opposed attack roll to avoid being disarmed if such an attempt fails). You can also use this weapon to make trip attacks. If you are tripped during your own trip attempt, you can drop the dire flail to avoid being tripped.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(81,'Hammer, gnome hooked','Weapons','Exotic Weapons','Two-Handed Melee Weapons','20 gp','1d6/1d4',NULL,NULL,'1d8/1d6','6 lb.','x3/x4',NULL,NULL,'-',NULL,'Bludgeoning and piercing',NULL,'A gnome hooked hammer is a double weapon. You can fight with it as if fighting with two weapons, but if you do, you incur all the normal attack penalties associated with fighting with two weapons, just as if you were using a one-handed weapon and a light weapon. The hammer''s blunt head is a bludgeoning weapon that deals 1d6 points of damage (crit x3). Its hook is a piercing weapon that deals 1d4 points of damage (crit x4). You can use either head as the primary weapon. The other head is the offhand weapon. A creature wielding a gnome hooked hammer in one hand can''t use it as a double weapon-only one end of the weapon can be used in any given round. You can use a gnome hooked hammer to make trip attacks. If you are tripped during your own trip attempt, you can drop the gnome hooked hammer to avoid being tripped. Gnomes treat gnome hooked hammers as martial weapons.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(82,'Sword, two-bladed','Weapons','Exotic Weapons','Two-Handed Melee Weapons','100 gp','1d6/1d6',NULL,NULL,'1d8/1d8','10 lb.','19-20/x2',NULL,NULL,'-',NULL,'Slashing',NULL,'A two-bladed sword is a double weapon. You can fight with it as if fighting with two weapons, but if you do, you incur all the normal attack penalties associated with fighting with two weapons, just as if you were using a one-handed weapon and a light weapon. A creature wielding a two-bladed sword in one hand can''t use it as a double weapon-only one end of the weapon can be used in any given round.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(83,'Urgrosh, dwarven','Weapons','Exotic Weapons','Two-Handed Melee Weapons','50 gp','1d6/1d4',NULL,NULL,'1d8/1d6','12 lb.','x3',NULL,NULL,'-',NULL,'Slashing or piercing',NULL,'A dwarven urgrosh is a double weapon. You can fight with it as if fighting with two weapons, but if you do, you incur all the normal attack penalties associated with fighting with two weapons, just as if you were using a one-handed weapon and a light weapon. The urgrosh''s axe head is a slashing weapon that deals 1d8 points of damage. Its spear head is a piercing weapon that deals 1d6 points of damage. You can use either head as the primary weapon. The other is the off-hand weapon. A creature wielding a dwarven urgrosh in one hand can''t use it as a double weapon-only one end of the weapon can be used in any given round. If you use a ready action to set an urgrosh against a charge, you deal double damage if you score a hit against a charging character. If you use an urgrosh against a charging character, the spear head is the part of the weapon that deals damage. Dwarves treat dwarven urgroshes as martial weapons.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(84,'Bolas','Weapons','Exotic Weapons','Ranged Weapons','5 gp','1d3',NULL,NULL,'1d4','2 lb.','x2',NULL,NULL,'10 ft.',NULL,'Bludgeoning',NULL,'You can use this weapon to make a ranged trip attack against an opponent. You can''t be tripped during your own trip attempt when using a set of bolas.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(85,'Crossbow, hand','Weapons','Exotic Weapons','Ranged Weapons','100 gp','1d3',NULL,NULL,'1d4','2 lb.','19-20/x2',NULL,NULL,'30 ft.',NULL,'Piercing',NULL,'You can draw a hand crossbow back by hand. Loading a hand crossbow is a move action that provokes attacks of opportunity. You can shoot, but not load, a hand crossbow with one hand at no penalty. You can shoot a hand crossbow with each hand, but you take a penalty on attack rolls as if attacking with two light weapons.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(86,'Bolts (10)','Weapons','Exotic Weapons','Ammunition','1 gp','-',NULL,NULL,'-','1 lb.','-',NULL,NULL,'-',NULL,'-',NULL,'A crossbow bolt used as a melee weapon is treated as a light improvised weapon (-4 penalty on attack rolls) and deals damage as a dagger of its size (crit x2). Bolts come in a wooden case that holds 10 bolts (or 5, for a repeating crossbow). A bolt that hits its target is destroyed; one that misses has a 50% chance of being destroyed or lost.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(87,'Crossbow, repeating heavy','Weapons','Exotic Weapons','Ranged Weapons','400 gp','1d8',NULL,NULL,'1d10','12 lb.','19-20/x2',NULL,NULL,'120 ft.',NULL,'Piercing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(88,'Bolts (5)','Weapons','Exotic Weapons','Ammunition','1 gp','-',NULL,NULL,'-','-','-',NULL,NULL,'1 lb.',NULL,NULL,NULL,'A crossbow bolt used as a melee weapon is treated as a light improvised weapon (-4 penalty on attack rolls) and deals damage as a dagger of its size (crit x2). Bolts come in a wooden case that holds 10 bolts (or 5, for a repeating crossbow). A bolt that hits its target is destroyed; one that misses has a 50% chance of being destroyed or lost.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(89,'Crossbow, repeating light','Weapons','Exotic Weapons','Ranged Weapons','250 gp','1d6',NULL,NULL,'1d8','6 lb.','19-20/x2',NULL,NULL,'80 ft.',NULL,'Piercing',NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(90,'Net','Weapons','Exotic Weapons','Ranged Weapons','20 gp','-',NULL,NULL,'-','-','10 ft.',NULL,NULL,'6 lb.',NULL,NULL,NULL,'A net is used to entangle enemies. When you throw a net, you make a ranged touch attack against your target. A net''s maximum range is 10 feet. If you hit, the target is entangled. An entangled creature takes a -2 penalty on attack rolls and a -4 penalty on Dexterity, can move at only half speed, and cannot charge or run. If you control the trailing rope by succeeding on an opposed Strength check while holding it, the entangled creature can move only within the limits that the rope allows. If the entangled creature attempts to cast a spell, it must make a DC 15 Concentration check or be unable to cast the spell. An entangled creature can escape with a DC 20 Escape Artist check (a full-round action). The net has 5 hit points and can be burst with a DC 25 Strength check (also a full-round action). A net is useful only against creatures within one size category of you. A net must be folded to be thrown effectively. The first time you throw your net in a fight, you make a normal ranged touch attack roll. After the net is unfolded, you take a -4 penalty on attack rolls with it. It takes 2 rounds for a proficient user to fold a net and twice that long for a nonproficient one to do so.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(91,'Shuriken (5)','Weapons','Exotic Weapons','Ammunition','1 gp','1',NULL,NULL,'1d2','1/2 lb.','x2',NULL,NULL,'10 ft.',NULL,'Piercing',NULL,'A shuriken is a special monk weapon. This designation gives a monk wielding shuriken special options. A shuriken can''t be used as a melee weapon. Although they are thrown weapons, shuriken are treated as ammunition for the purposes of drawing them, crafting masterwork or otherwise special versions of them and what happens to them after they are thrown.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(92,'Padded','Armor and Shields','Armor','Light armor','5 gp',NULL,'+1','+8',NULL,'10 lb.',NULL,'0','5%',NULL,'30 ft.',NULL,'20 ft.','','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(93,'Leather','Armor and Shields','Armor','Light armor','10 gp',NULL,'+2','+6',NULL,'15 lb.',NULL,'0','10%',NULL,'30 ft.',NULL,'20 ft.','','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(94,'Studded leather','Armor and Shields','Armor','Light armor','25 gp',NULL,'+3','+5',NULL,'20 lb.',NULL,'-1','15%',NULL,'30 ft.',NULL,'20 ft.','','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(95,'Chain shirt','Armor and Shields','Armor','Light armor','100 gp',NULL,'+4','+4',NULL,'25 lb.',NULL,'-2','20%',NULL,'30 ft.',NULL,'20 ft.','A chain shirt comes with a steel cap.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(96,'Hide','Armor and Shields','Armor','Medium armor','15 gp',NULL,'+3','+4',NULL,'25 lb.',NULL,'-3','20%',NULL,'20 ft.',NULL,'15 ft.','','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(97,'Scale mail','Armor and Shields','Armor','Medium armor','50 gp',NULL,'+4','+3',NULL,'30 lb.',NULL,'-4','25%',NULL,'20 ft.',NULL,'15 ft.','The suit includes gauntlets.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(98,'Chainmail','Armor and Shields','Armor','Medium armor','150 gp',NULL,'+5','+2',NULL,'40 lb.',NULL,'-5','30%',NULL,'20 ft.',NULL,'15 ft.','The suit includes gauntlets.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(99,'Breastplate','Armor and Shields','Armor','Medium armor','200 gp',NULL,'+5','+3',NULL,'30 lb.',NULL,'-4','25%',NULL,'20 ft.',NULL,'15 ft.','It comes with a helmet and greaves.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(100,'Splint mail','Armor and Shields','Armor','Heavy armor','200 gp',NULL,'+6','+0',NULL,'45 lb.',NULL,'-7','40%',NULL,'20 ft.',NULL,'15 ft.','The suit includes gauntlets.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(101,'Banded mail','Armor and Shields','Armor','Heavy armor','250 gp',NULL,'+6','+1',NULL,'35 lb.',NULL,'-6','35%',NULL,'20 ft.',NULL,'15 ft.','The suit includes gauntlets.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(102,'Half-plate','Armor and Shields','Armor','Heavy armor','600 gp',NULL,'+7','+0',NULL,'50 lb.',NULL,'-7','40%',NULL,'20 ft.',NULL,'15 ft.','The suit includes gauntlets.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(103,'Full plate','Armor and Shields','Armor','Heavy armor','1,500 gp',NULL,'+8','+1',NULL,'50 lb.',NULL,'-6','35%',NULL,'20 ft.',NULL,'15 ft.','The suit includes gauntlets, heavy leather boots, a visored helmet, and a thick layer of padding that is worn underneath the armor. Each suit of full plate must be individually fitted to its owner by a master armorsmith, although a captured suit can be resized to fit a new owner at a cost of 200 to 800 (2d4x100) gold pieces.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(104,'Buckler','Armor and Shields','Armor','Shields','15 gp',NULL,'+1','-',NULL,'5 lb.',NULL,'-1','5%',NULL,'-',NULL,'-','This small metal shield is worn strapped to your forearm. You can use a bow or crossbow without penalty while carrying it. You can also use your shield arm to wield a weapon (whether you are using an off-hand weapon or using your off hand to help wield a two-handed weapon), but you take a -1 penalty on attack rolls while doing so. This penalty stacks with those that may apply for fighting with your off hand and for fighting with two weapons. In any case, if you use a weapon in your off hand, you don''t get the buckler''s AC bonus for the rest of the round. You can''t bash someone with a buckler.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(105,'Shield, light wooden','Armor and Shields','Armor','Shields','3 gp',NULL,'+1','-',NULL,'5 lb.',NULL,'-1','5%',NULL,'-',NULL,'-','You strap a shield to your forearm and grip it with your hand. A light shield''s weight lets you carry other items in that hand, although you cannot use weapons with it. Wooden or Steel: Wooden and steel shields offer the same basic protection, though they respond differently to special attacks. Shield Bash Attacks: You can bash an opponent with a light shield, using it as an off-hand weapon. See Table: Weapons for the damage dealt by a shield bash. Used this way, a light shield is a martial bludgeoning weapon. For the purpose of penalties on attack rolls, treat a light shield as a light weapon. If you use your shield as a weapon, you lose its AC bonus until your next action (usually until the next round). An enhancement bonus on a shield does not improve the effectiveness of a shield bash made with it, but the shield can be made into a magic weapon in its own right.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(106,'Shield, light steel','Armor and Shields','Armor','Shields','9 gp',NULL,'+1','-',NULL,'6 lb.',NULL,'-1','5%',NULL,'-',NULL,'-','You strap a shield to your forearm and grip it with your hand. A light shield''s weight lets you carry other items in that hand, although you cannot use weapons with it. Wooden or Steel: Wooden and steel shields offer the same basic protection, though they respond differently to special attacks. Shield Bash Attacks: You can bash an opponent with a light shield, using it as an off-hand weapon. See Table: Weapons for the damage dealt by a shield bash. Used this way, a light shield is a martial bludgeoning weapon. For the purpose of penalties on attack rolls, treat a light shield as a light weapon. If you use your shield as a weapon, you lose its AC bonus until your next action (usually until the next round). An enhancement bonus on a shield does not improve the effectiveness of a shield bash made with it, but the shield can be made into a magic weapon in its own right.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(107,'Shield, heavy wooden','Armor and Shields','Armor','Shields','7 gp',NULL,'+2','-',NULL,'10 lb.',NULL,'-2','15%',NULL,'-',NULL,'-','You strap a shield to your forearm and grip it with your hand. A heavy shield is so heavy that you can''t use your shield hand for anything else. Wooden or Steel: Wooden and steel shields offer the same basic protection, though they respond differently to special attacks. Shield Bash Attacks: You can bash an opponent with a heavy shield, using it as an off-hand weapon. See Table: Weapons for the damage dealt by a shield bash. Used this way, a heavy shield is a martial bludgeoning weapon. For the purpose of penalties on attack rolls, treat a heavy shield as a one-handed weapon. If you use your shield as a weapon, you lose its AC bonus until your next action (usually until the next round). An enhancement bonus on a shield does not improve the effectiveness of a shield bash made with it, but the shield can be made into a magic weapon in its own right.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(108,'Shield, heavy steel','Armor and Shields','Armor','Shields','20 gp',NULL,'+2','-',NULL,'15 lb.',NULL,'-2','15%',NULL,'-',NULL,'-','You strap a shield to your forearm and grip it with your hand. A heavy shield is so heavy that you can''t use your shield hand for anything else. Wooden or Steel: Wooden and steel shields offer the same basic protection, though they respond differently to special attacks. Shield Bash Attacks: You can bash an opponent with a heavy shield, using it as an off-hand weapon. See Table: Weapons for the damage dealt by a shield bash. Used this way, a heavy shield is a martial bludgeoning weapon. For the purpose of penalties on attack rolls, treat a heavy shield as a one-handed weapon. If you use your shield as a weapon, you lose its AC bonus until your next action (usually until the next round). An enhancement bonus on a shield does not improve the effectiveness of a shield bash made with it, but the shield can be made into a magic weapon in its own right.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(109,'Shield, tower','Armor and Shields','Armor','Shields','30 gp',NULL,'+4','+2',NULL,'45 lb.',NULL,'-10','50%',NULL,'-',NULL,'-','This massive wooden shield is nearly as tall as you are. In most situations, it provides the indicated shield bonus to your AC. However, you can instead use it as total cover, though you must give up your attacks to do so. The shield does not, however, provide cover against targeted spells; a spellcaster can cast a spell on you by targeting the shield you are holding. You cannot bash with a tower shield, nor can you use your shield hand for anything else. When employing a tower shield in combat, you take a -2 penalty on attack rolls because of the shield''s encumbrance.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(110,'Armor spikes','Armor and Shields','Armor','Extras','+50 gp',NULL,'-','-',NULL,'+10 lb.',NULL,'-','-',NULL,'-',NULL,'-','You can have spikes added to your armor, which allow you to deal extra piercing damage (see Table: Weapons) on a successful grapple attack. The spikes count as a martial weapon. If you are not proficient with them, you take a -4 penalty on grapple checks when you try to use them. You can also make a regular melee attack (or off-hand attack) with the spikes, and they count as a light weapon in this case. (You can''t also make an attack with armor spikes if you have already made an attack with another off-hand weapon, and vice versa.)An enhancement bonus to a suit of armor does not improve the spikes'' effectiveness, but the spikes can be made into magic weapons in their own right.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(111,'Gauntlet, locked','Armor and Shields','Armor','Extras','8 gp',NULL,'-','-',NULL,NULL,NULL,'Special','-',NULL,'-',NULL,'+5 lb.','This armored gauntlet has small chains and braces that allow the wearer to attach a weapon to the gauntlet so that it cannot be dropped easily. It provides a +10 bonus on any roll made to keep from being disarmed in combat. Removing a weapon from a locked gauntlet or attaching a weapon to a locked gauntlet is a full-round action that provokes attacks of opportunity. The price given is for a single locked gauntlet. The weight given applies only if you''re wearing a breastplate, light armor, or no armor. Otherwise, the locked gauntlet replaces a gauntlet you already have as part of the armor. While the gauntlet is locked, you can''t use the hand wearing it for casting spells or employing skills. (You can still cast spells with somatic components, provided that your other hand is free.)Like a normal gauntlet, a locked gauntlet lets you deal lethal damage rather than nonlethal damage with an unarmed strike.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(112,'Shield spikes','Armor and Shields','Armor','Extras','+10 gp',NULL,'-','-',NULL,'+5 lb.',NULL,'-','-',NULL,'-',NULL,'-','When added to your shield, these spikes turn it into a martial piercing weapon that increases the damage dealt by a shield bash as if the shield were designed for a creature one size category larger than you. You can''t put spikes on a buckler or a tower shield. Otherwise, attacking with a spiked shield is like making a shield bash attack (see above). An enhancement bonus on a spiked shield does not improve the effectiveness of a shield bash made with it, but a spiked shield can be made into a magic weapon in its own right.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(113,'Backpack (empty)','Goods and Services','Item','Adventuring Gear','2 gp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(114,'Barrel (empty)','Goods and Services','Item','Adventuring Gear','2 gp',NULL,NULL,NULL,NULL,'30 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(115,'Basket (empty)','Goods and Services','Item','Adventuring Gear','4 sp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(116,'Bedroll','Goods and Services','Item','Adventuring Gear','1 sp',NULL,NULL,NULL,NULL,'5 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(117,'Bell','Goods and Services','Item','Adventuring Gear','1 gp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(118,'Blanket, winter','Goods and Services','Item','Adventuring Gear','5 sp',NULL,NULL,NULL,NULL,'3 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(119,'Block and tackle','Goods and Services','Item','Adventuring Gear','5 gp',NULL,NULL,NULL,NULL,'5 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(120,'Bottle, wine, glass','Goods and Services','Item','Adventuring Gear','2 gp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(121,'Bucket (empty)','Goods and Services','Item','Adventuring Gear','5 sp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(122,'Caltrops','Goods and Services','Item','Adventuring Gear','1 gp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'A caltrop is a four-pronged iron spike crafted so that one prong faces up no matter how the caltrop comes to rest. You scatter caltrops on the ground in the hope that your enemies step on them or are at least forced to slow down to avoid them. One 2- pound bag of caltrops covers an area 5 feet square. Each time a creature moves into an area covered by caltrops (or spends a round fighting while standing in such an area), it might step on one. The caltrops make an attack roll (base attack bonus +0) against the creature. For this attack, the creature''s shield, armor, and deflection bonuses do not count. If the creature is wearing shoes or other footwear, it gets a +2 armor bonus to AC. If the caltrops succeed on the attack, the creature has stepped on one. The caltrop deals 1 point of damage, and the creature''s speed is reduced by one-half because its foot is wounded. This movement penalty lasts for 24 hours, or until the creature is successfully treated with a DC 15 Heal check, or until it receives at least 1 point of magical curing. A charging or running creature must immediately stop if it steps on a caltrop. Any creature moving at half speed or slower can pick its way through a bed of caltrops with no trouble. Caltrops may not be effective against unusual opponents.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(123,'Candle','Goods and Services','Item','Adventuring Gear','1 cp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'A candle dimly illuminates a 5-foot radius and burns for 1 hour.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(124,'Canvas (sq. yd.)','Goods and Services','Item','Adventuring Gear','1 sp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(125,'Case, map or scroll','Goods and Services','Item','Adventuring Gear','1 gp',NULL,NULL,NULL,NULL,'1/2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(126,'Chain (10 ft.)','Goods and Services','Item','Adventuring Gear','30 gp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Chain has hardness 10 and 5 hit points. It can be burst with a DC 26 Strength check.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(127,'Chalk, 1 piece','Goods and Services','Item','Adventuring Gear','1 cp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(128,'Chest (empty)','Goods and Services','Item','Adventuring Gear','2 gp',NULL,NULL,NULL,NULL,'25 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(129,'Crowbar','Goods and Services','Item','Adventuring Gear','2 gp',NULL,NULL,NULL,NULL,'5 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'A crowbar it grants a +2 circumstance bonus on Strength checks made for such purposes. If used in combat, treat a crowbar as a one-handed improvised weapon that deals bludgeoning damage equal to that of a club of its size.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(130,'Firewood (per day)','Goods and Services','Item','Adventuring Gear','1 cp',NULL,NULL,NULL,NULL,'20 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(131,'Fishhook','Goods and Services','Item','Adventuring Gear','1 sp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(132,'Fishing net, 25 sq. ft.','Goods and Services','Item','Adventuring Gear','4 gp',NULL,NULL,NULL,NULL,'5 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(133,'Flask (empty)','Goods and Services','Item','Adventuring Gear','3 cp',NULL,NULL,NULL,NULL,'1-1/2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(134,'Flint and steel','Goods and Services','Item','Adventuring Gear','1 gp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Lighting a torch with flint and steel is a full-round action, and lighting any other fire with them takes at least that long.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(135,'Grappling hook','Goods and Services','Item','Adventuring Gear','1 gp',NULL,NULL,NULL,NULL,'4 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Throwing a grappling hook successfully requires a Use Rope check (DC 10, +2 per 10 feet of distance thrown).','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(136,'Hammer','Goods and Services','Item','Adventuring Gear','5 sp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'If a hammer is used in combat, treat it as a one-handed improvised weapon that deals bludgeoning damage equal to that of a spiked gauntlet of its size.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(137,'Ink (1 oz. vial)','Goods and Services','Item','Adventuring Gear','8 gp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'This is black ink. You can buy ink in other colors, but it costs twice as much.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(138,'Inkpen','Goods and Services','Item','Adventuring Gear','1 sp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(139,'Jug, clay','Goods and Services','Item','Adventuring Gear','3 cp',NULL,NULL,NULL,NULL,'9 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'This basic ceramic jug is fitted with a stopper and holds 1 gallon of liquid.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(140,'Ladder, 10-foot','Goods and Services','Item','Adventuring Gear','5 cp',NULL,NULL,NULL,NULL,'20 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(141,'Lamp, common','Goods and Services','Item','Adventuring Gear','1 sp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'A lamp clearly illuminates a 15-foot radius, provides shadowy illumination out to a 30-foot radius, and burns for 6 hours on a pint of oil. You can carry a lamp in one hand.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(142,'Lantern, bullseye','Goods and Services','Item','Adventuring Gear','12 gp',NULL,NULL,NULL,NULL,'3 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'A bullseye lantern provides clear illumination in a 60-foot cone and shadowy illumination in a 120-foot cone. It burns for 6 hours on a pint of oil. You can carry a bullseye lantern in one hand.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(143,'Lantern, hooded','Goods and Services','Item','Adventuring Gear','7 gp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'A hooded lantern clearly illuminates a 30-foot radius and provides shadowy illumination in a 60-foot radius. It burns for 6 hours on a pint of oil. You can carry a hooded lantern in one hand.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(144,'Lock, Very simple','Goods and Services','Item','Adventuring Gear','20 gp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'The DC to open a lock with the Open Lock skill depends on the lock''s quality: simple (DC 20), average (DC 25), good (DC 30), or superior (DC 40).','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(145,'Lock, Average','Goods and Services','Item','Adventuring Gear','40 gp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'The DC to open a lock with the Open Lock skill depends on the lock''s quality: simple (DC 20), average (DC 25), good (DC 30), or superior (DC 40).','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(146,'Lock, Good','Goods and Services','Item','Adventuring Gear','80 gp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'The DC to open a lock with the Open Lock skill depends on the lock''s quality: simple (DC 20), average (DC 25), good (DC 30), or superior (DC 40).','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(147,'Lock, Amazing','Goods and Services','Item','Adventuring Gear','150 gp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'The DC to open a lock with the Open Lock skill depends on the lock''s quality: simple (DC 20), average (DC 25), good (DC 30), or superior (DC 40).','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(148,'Manacles','Goods and Services','Item','Adventuring Gear','15 gp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Manacles can bind a Medium creature. A manacled creature can use the Escape Artist skill to slip free (DC 30, or DC 35 for masterwork manacles). Breaking the manacles requires a Strength check (DC 26, or DC 28 for masterwork manacles). Manacles have hardness 10 and 10 hit points. Most manacles have locks; add the cost of the lock you want to the cost of the manacles. For the same cost, you can buy manacles for a Small creature. For a Large creature, manacles cost ten times the indicated amount, and for a Huge creature, one hundred times this amount. Gargantuan, Colossal, Tiny, Diminutive, and Fine creatures can be held only by specially made manacles.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(149,'Manacles, masterwork','Goods and Services','Item','Adventuring Gear','50 gp',NULL,NULL,NULL,NULL,'2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Manacles can bind a Medium creature. A manacled creature can use the Escape Artist skill to slip free (DC 30, or DC 35 for masterwork manacles). Breaking the manacles requires a Strength check (DC 26, or DC 28 for masterwork manacles). Manacles have hardness 10 and 10 hit points. Most manacles have locks; add the cost of the lock you want to the cost of the manacles. For the same cost, you can buy manacles for a Small creature. For a Large creature, manacles cost ten times the indicated amount, and for a Huge creature, one hundred times this amount. Gargantuan, Colossal, Tiny, Diminutive, and Fine creatures can be held only by specially made manacles.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(150,'Mirror, small steel','Goods and Services','Item','Adventuring Gear','10 gp',NULL,NULL,NULL,NULL,'1/2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(151,'Mug/Tankard, clay','Goods and Services','Item','Adventuring Gear','2 cp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(152,'Oil (1-pint flask)','Goods and Services','Item','Adventuring Gear','1 sp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'A pint of oil burns for 6 hours in a lantern. You can use a flask of oil as a splash weapon. Use the rules for alchemist''s fire, except that it takes a full round action to prepare a flask with a fuse. Once it is thrown, there is a 50% chance of the flask igniting successfully. You can pour a pint of oil on the ground to cover an area 5 feet square, provided that the surface is smooth. If lit, the oil burns for 2 rounds and deals 1d3 points of fire damage to each creature in the area.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(153,'Paper (sheet)','Goods and Services','Item','Adventuring Gear','4 sp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(154,'Parchment (sheet)','Goods and Services','Item','Adventuring Gear','2 sp',NULL,NULL,NULL,NULL,'-',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(155,'Pick, miner''s','Goods and Services','Item','Adventuring Gear','3 gp',NULL,NULL,NULL,NULL,'10 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(156,'Pitcher, clay','Goods and Services','Item','Adventuring Gear','2 cp',NULL,NULL,NULL,NULL,'5 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(157,'Piton','Goods and Services','Item','Adventuring Gear','1 sp',NULL,NULL,NULL,NULL,'1/2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(158,'Pole, 10-foot','Goods and Services','Item','Adventuring Gear','2 sp',NULL,NULL,NULL,NULL,'8 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(159,'Pot, iron','Goods and Services','Item','Adventuring Gear','5 sp',NULL,NULL,NULL,NULL,'10 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(160,'Pouch, belt (empty)','Goods and Services','Item','Adventuring Gear','1 gp',NULL,NULL,NULL,NULL,'1/2 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(161,'Ram, portable','Goods and Services','Item','Adventuring Gear','10 gp',NULL,NULL,NULL,NULL,'20 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'This iron-shod wooden beam gives you a +2 circumstance bonus on Strength checks made to break open a door and it allows a second person to help you without having to roll, increasing your bonus by 2.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(162,'Rations, trail (per day)','Goods and Services','Item','Adventuring Gear','5 sp',NULL,NULL,NULL,NULL,'1 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(163,'Rope, hempen (50 ft.)','Goods and Services','Item','Adventuring Gear','1 gp',NULL,NULL,NULL,NULL,'10 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'This rope has 2 hit points and can be burst with a DC 23 Strength check.','SRD 3.5 Equipment');
INSERT INTO "equipment" VALUES(164,'Rope, silk (50 ft.)','Goods and Services','Item','Adventuring Gear','10 gp',NULL,NULL,NULL,NULL,'5 lb.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'This rope has 4 hit points and can be burst with a DC 24 Strength check. It is so supple that it provides a +2 circumstance bonus on Use Rope checks.','SRD 3.5 Equipment');
