INSERT INTO "item" VALUES(121,'Amulet of Epic Natural Armor +10','Wondrous','Epic','No',NULL,'20','2,000,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, barkskin, creator''s caster level must be three times the amulet''s bonus',NULL,'-','\n      <div topic=\"Amulet of Epic Natural Armor\" level=\"8\"><p><b>Amulet of Epic Natural Armor:</b>  This amulet toughens the wearer''s body and flesh, giving him or her an enhancement bonus to his or her natural armor of +6 or higher, depending on the type of amulet.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>barkskin</i>,  creator''s caster level must be three times the amulet''s bonus; <i>Market Price:</i> 720,000 gp (+6), 980,000 gp (+7), 1,280,000 gp (+8), 1,620,000 gp (+9), 2,000,000 gp (+10); <i>Weight:</i> -.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(122,'Belt of Epic Strength +8','Wondrous','Epic','No',NULL,'20','640,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, bull''s strength',NULL,'1 lb','\n      <div topic=\"Belt of Epic Strength\" level=\"8\"><p><b>Belt of Epic Strength:</b> This belt adds an enhancement bonus of +8 or higher to the wearer''s Strength score.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>bull''s strength; Market Price:</i> 640,000 gp (+8), 1,000,000 gp (+10), 1,440,000 gp (+12); <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(123,'Belt of Epic Strength +10','Wondrous','Epic','No',NULL,'20','1,000,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, bull''s strength',NULL,'1 lb','\n      <div topic=\"Belt of Epic Strength\" level=\"8\"><p><b>Belt of Epic Strength:</b> This belt adds an enhancement bonus of +8 or higher to the wearer''s Strength score.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>bull''s strength; Market Price:</i> 640,000 gp (+8), 1,000,000 gp (+10), 1,440,000 gp (+12); <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(124,'Belt of Epic Strength +12','Wondrous','Epic','No',NULL,'20','1,440,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, bull''s strength',NULL,'1 lb','\n      <div topic=\"Belt of Epic Strength\" level=\"8\"><p><b>Belt of Epic Strength:</b> This belt adds an enhancement bonus of +8 or higher to the wearer''s Strength score.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>bull''s strength; Market Price:</i> 640,000 gp (+8), 1,000,000 gp (+10), 1,440,000 gp (+12); <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(125,'Boots of Swiftness','Wondrous','Epic','No',NULL,'20','256,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, cat''s grace, expeditious retreat, haste, jump',NULL,'1 lb','\n      <div topic=\"Boots of Swiftness\" level=\"8\"><p><b>Boots of Swiftness:</b> These shoes grant their wearer a +6 enhancement bonus to Dexterity. The wearer''s speed doubles (this does not stack with any magical or supernatural enhancement to speed), he or she gains the evasion ability (as the rogue class feature), and the wearer''s jumping distance is not limited by his or her height. The wearer gains a +20 competence bonus on Balance, Climb, Jump, and Tumble checks. Three times per day, the wearer can utter a command word to activate the boots'' <i>haste</i> power (as the <i>haste</i> spell, lasts 20 rounds).</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>cat''s grace</i>,  <i>expeditious retreat</i>,  <i>haste</i>,  <i>jump; Market Price:</i> 256,000 gp; <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(126,'Bracers of Epic Armor +11','Wondrous','Epic','No',NULL,'20','1,210,000',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, mage armor, creator''s caster level must be twice the bracers'' bonus',NULL,'1 lb','\n      <div topic=\"Bracers of Epic Armor\" level=\"8\"><p><b>Bracers of Epic Armor:</b> These items surround the wearer with an invisible but tangible field of force, granting him or her an armor bonus of +11 or higher, just as though he or she were wearing armor. Both bracers must be worn for the magic to be effective.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>mage armor</i>,  creator''s caster level must be twice the bracers'' bonus; <i>Market Price:</i> 1,210,000 (+11), 1,440,000 (+12), 1,690,000 gp (+13), 1,960,000 (+14), 2,250,000 gp (+15); <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
