INSERT INTO "item" VALUES(134,'Bracers of Relentless Might','Wondrous','Epic','No',NULL,'20','4,384,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, bull''s strength, endurance, enlarge',NULL,'1 lb','\n      <div topic=\"Bracers of Relentless Might\" level=\"8\"><p><b>Bracers of Relentless Might:</b> These bracers grant a +12 enhancement bonus to the wearer''s Strength and Constitution. The wearer is treated as two size categories larger than normal (to a maximum of Colossal) for purposes of combat-related opposed checks that apply a modifier based on size, such as bull rush, grapple, and trip.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>bull''s strength</i>,  <i>endurance</i>,  <i>enlarge; Market Price:</i> 4,384,000 gp; <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(135,'Cabinet of Feasting','Wondrous','Epic','No',NULL,'40','288,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, heroes'' feast',NULL,'20 lb','\n      <div topic=\"Cabinet of Feasting\" level=\"8\"><p><b>Cabinet of Feasting:</b> This cabinet has the ability to produce a feast for up to forty people, three times per day. Merely opening the doors of the cabinet reveals platters of food of all types and flavors of the very freshest sort. The meal has all the qualities and benefits of that produced by a <i>heroes'' feast</i> spell.</p><p><i>Caster Level:</i> 40th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>heroes'' feast; Market Price:</i> 288,000 gp; <i>Weight:</i> 20 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(136,'Cloak of Epic Charisma +8','Wondrous','Epic','No',NULL,'20','640,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, charm monster',NULL,'1 lb','\n      <div topic=\"Cloak of Epic Charisma\" level=\"8\"><p><b>Cloak of Epic Charisma:</b> When worn, this cloak grants a +8 or higher enhancement bonus to a character''s Charisma score.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>charm monster; Market Price:</i> 640,000 gp (+8), 1,000,000 gp (+10), 1,440,000 gp (+12); <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
