INSERT INTO "item" VALUES(139,'Cloak of Epic Resistance +6','Wondrous','Epic','No',NULL,'20','360,000',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, resistance, creator''s caster level must be three times the cloak''s bonus',NULL,'1 lb','\n      <div topic=\"Cloak of Epic Resistance\" level=\"8\"><p><b>Cloak of Epic Resistance:</b> These garments offer magic protection in the form of a +6 or higher resistance bonus on all saving throws (Fortitude, Reflex, and Will).</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>resistance</i>,  creator''s caster level must be three times the cloak''s bonus; <i>Market Price:</i> 360,000 (+6), 490,000 (+7), 640,000 gp (+8), 810,000 (+9), 1,000,000 gp (+10); <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
