INSERT INTO "item" VALUES(151,'Horseshoes of the Peerless Steed','Wondrous','Epic','No',NULL,'20','217,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, Ride skill, haste, spell resistance',NULL,'1 lb. each','\n      <div topic=\"Horseshoes of the Peerless Steed\" level=\"8\"><p><b>Horseshoes of the Peerless Steed:</b> These horseshoes magically adhere to the feet of any hoofed creature. Anyone riding the creature gains a +10 competence bonus on Ride checks and is treated as having ranks in the appropriate Ride skill (and thus does not take the -5 penalty for riding an unfamiliar mount). The horseshoes grant the creature (or its rider, as appropriate) the effects of the Trample, Ride-by Attack, and Spirited Charge feats. The wearer of the horseshoes gains spell resistance 32 against enchantment effects. In addition, the ground speed of the creature wearing the horseshoes doubles.</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, Ride skill, <i>haste</i>,  <i>spell resistance; Market Price:</i> 217,000 gp; <i>Weight:</i> 1 lb. each.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(152,'Mantle of Epic Spell Resistance','Wondrous','Epic','No',NULL,'29','290,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, spell resistance',NULL,'1 lb','\n      <div topic=\"Mantle of Epic Spell Resistance\" level=\"8\"><p><b>Mantle of Epic Spell Resistance:</b> This garment is worn over normal clothing or armor. It grants the wearer spell resistance 40.</p><p><i>Caster Level:</i> 29th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>spell resistance; Market Price:</i> 290,000 gp; <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
INSERT INTO "item" VALUES(153,'Mantle of Great Stealth','Wondrous','Epic','No',NULL,'20','242,000 gp',NULL,'Craft Wondrous Item, Craft Epic Wondrous Item, blur, invisibility, nondetection, silence',NULL,'1 lb','\n      <div topic=\"Mantle of Great Stealth\" level=\"8\"><p><b>Mantle of Great Stealth:</b> The wearer of this cloak gains a +30 bonus on Hide and Move Silently checks. The wearer''s outline is blurry and indistinct, granting concealment (20% miss chance) at all times (as the <i>blur</i> spell). The mantle also grants nondetection to its wearer (as the spell).</p><p><i>Caster Level:</i> 20th; <i>Prerequisites:</i> Craft Wondrous Item, Craft Epic Wondrous Item, <i>blur</i>,  <i>invisibility</i>,  <i>nondetection</i>,  <i>silence; Market Price:</i> 242,000 gp; <i>Weight:</i> 1 lb.</p>\n</div>\n    ','SRD 3.5 EpicMagicItems2');
