INSERT INTO "item" VALUES(438,'Metamagic, Quicken, Lesser','Rod',NULL,'No','Strong (no school)','17','35,000 gp',NULL,'Craft Rod, Quicken Spell',NULL,NULL,'\n      <div topic=\"Metamagic, Quicken\" level=\"8\"><p><b>Metamagic, Quicken:</b> The wielder can cast up to three spells per day that are quickened as though using the Quicken Spell feat.</p><p>Strong (no school); CL 17th; Craft Rod, Quicken Spell; Price 35,000 gp (lesser), 75,500 gp (normal), 170,000 gp (greater).</p>\n</div>\n    ','SRD 3.5 MagicItemsIII');
