INSERT INTO "item" VALUES(1289,'Amulet of Health +2','Wondrous',NULL,'No','Moderate transmutation','8','4,000 gp',NULL,'Craft Wondrous Item, bear''s endurance',NULL,NULL,'\n      <div topic=\"Amulet of Health\" level=\"8\"><p><b>Amulet of Health:</b> This amulet is a golden disk on a chain. It usually bears the image of a lion or other powerful animal. The amulet grants the wearer an enhancement bonus to Constitution of +2, +4, or +6.</p><p>Moderate transmutation; CL 8th; Craft Wondrous Item, <i>bear''s endurance</i>; Price 4,000 gp (+2), 16,000 gp (+4), 36,000 gp (+6).</p>\n</div>\n    ','SRD 3.5 MagicItemsV');
INSERT INTO "item" VALUES(1290,'Amulet of Health +4','Wondrous',NULL,'No','Moderate transmutation','8','16,000 gp',NULL,'Craft Wondrous Item, bear''s endurance',NULL,NULL,'\n      <div topic=\"Amulet of Health\" level=\"8\"><p><b>Amulet of Health:</b> This amulet is a golden disk on a chain. It usually bears the image of a lion or other powerful animal. The amulet grants the wearer an enhancement bonus to Constitution of +2, +4, or +6.</p><p>Moderate transmutation; CL 8th; Craft Wondrous Item, <i>bear''s endurance</i>; Price 4,000 gp (+2), 16,000 gp (+4), 36,000 gp (+6).</p>\n</div>\n    ','SRD 3.5 MagicItemsV');
